'use strict';

/**
* Evaluate the beta function.
*
* @module @stdlib/math/base/special/beta
*
* @example
* var beta = require( '@stdlib/math/base/special/beta' );
*
* var v = beta( 0, 0 );
* // returns +Infinity
*
* v = beta( 1, 1 );
* // returns 1
*
* v = beta( -1, 2 );
* // return NaN
*
* v = beta( 5, 0.2 );
* // returns ~3.382
*
* v = beta( 4, 1 );
* // returns 0.25
*
* v = beta( NaN, 2 );
* // returns NaN
*/

// MODULES //

var beta = require( './beta.js' );


// EXPORTS //

module.exports = beta;
