#include <random>
#include <algorithm>
#include <iterator>
#include <vector>
#include <iostream>
#include <boost/math/special_functions/beta.hpp>

using namespace std;

vector<double> linspace( double start, double end, int num ) {
	double delta = (end - start) / (num - 1);
	vector<double> arr( num - 1 );
	for ( int i = 0; i < num - 1; ++i ){
		arr[ i ] = start + delta * i;
	}
	arr.push_back( end );
	return arr;
}

void print_vector( vector<double> vec, bool last = false ) {
	cout << "[";
	for ( vector<double>::iterator it = vec.begin(); it != vec.end(); ++it ) {
		if ( vec.end() != it+1 ) {
			cout << setprecision (16) << *it;
			cout << ",";
		} else {
			cout << setprecision (16) << *it;
			cout << "]";
			if ( last == false ) {
				cout << ",";
			}
		}
	}
	return;
}

void print_results(
	vector<double> a,
	vector<double> b,
	vector<double> expected
) {
	cout << "{" << endl;
	cout << "  \"a\": ";
	print_vector( a );
	cout << "  \"b\": ";
	print_vector( b );
	cout << "  \"expected\": ";
	print_vector( expected, true );
	cout << "}" << endl;
	return;
}

int main() {
	random_device rd;
	mt19937 g(rd());

	vector<double> a = linspace( 0.1, 100.0, 2000 );
	shuffle( a.begin(), a.end(), g );
	vector<double> b = linspace( 0.1, 100.0, 2000 );
	shuffle( b.begin(), b.end(), g );
	vector<double> expected;

	for ( int i = 0; i < 2000; i++ ) {
		double arg1 = a[ i ];
		double arg2 = b[ i ];
		expected.push_back( boost::math::beta( arg1, arg2 ) );
	}

	print_results( a, b, expected );
	return 0;
}
