/**
* Restrict a value to a specified range.
*/
#include <math.h>
#include "isnan.h"
#include "is_negative_zero.h"
#include "clamp.h"

/**
* Restricts a value to a specified range.
*
* @param v       number
* @param min     minimum value
* @param max     maximum value
* @return        restricted value
*/
double stdlib_base_clamp( const double v, const double min, const double max ) {
	if (
		stdlib_base_isnan( v ) ||
		stdlib_base_isnan( min ) ||
		stdlib_base_isnan( max )
	) {
		return NAN;
	}
	// Simple cases...
	if ( v < min ) {
		return min;
	}
	if ( v > max ) {
		return max;
	}
	// Special cases for handling +-0.0...
	if ( min == 0.0 && stdlib_base_is_negative_zero( v ) ) {
		return min; // +-0.0
	}
	if ( v == 0.0 && stdlib_base_is_negative_zero( max ) ) {
		return max; // -0.0
	}
	// Case: min <= v <= max
	return v;
}
