'use strict';

// MODULES //

var tape = require( 'tape' );
var isnan = require( '@stdlib/math/base/utils/is-nan' );
var abs = require( '@stdlib/math/base/special/abs' );
var PINF = require( '@stdlib/math/constants/float64-pinf' );
var NINF = require( '@stdlib/math/constants/float64-ninf' );
var cos = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof cos, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function computes the cosine', function test( t ) {
	var delta;

	delta = abs( cos( 0.0 ) - 1.0 );
	t.ok( delta < 1e-14, 'approx 1' );

	delta = abs( cos( Math.PI/2.0 ) - 0.0 );
	t.ok( delta < 1e-14, 'approx 0' );

	delta = abs( cos( Math.PI/3.0 ) - 0.5 );
	t.ok( delta < 1e-14, 'approx 1/2' );

	delta = abs( cos( Math.PI/4.0 ) - Math.sqrt(2.0)/2.0 );
	t.ok( delta < 1e-14, 'approx sqrt(2)/2' );

	t.end();
});

tape( 'the function returns `NaN` if provided a `NaN`', function test( t ) {
	var v = cos( NaN );
	t.equal( isnan( v ), true, 'returns NaN' );
	t.end();
});

tape( 'the function returns `NaN` if provided a `+infinity`', function test( t ) {
	var v = cos( PINF );
	t.equal( isnan( v ), true, 'returns NaN' );
	t.end();
});

tape( 'the function returns `NaN` if provided a `-infinity`', function test( t ) {
	var v = cos( NINF );
	t.equal( isnan( v ), true, 'returns NaN' );
	t.end();
});
