/**
* Convert an angle from degrees to radians.
*/
#include <math.h>
#include "deg2rad.h"

// Ï€ / 180.0
#define PI_DIV_180 1.7453292519943295e-2

/**
* Converts an angle from degrees to radians.
*
* @param x       angle in degrees
* @return        angle in radians
*/
double stdlib_deg2rad( const double x ) {
	return x * PI_DIV_180;
}
