# To run this script, `cd` to the `./test/fixtures` directory and then, from the R shell, `source("./runner.R")`.
options( digits = 16 );
library( jsonlite );

x = seq( -6.5, 25, 0.5 )
y = digamma( x )

cat( y, sep = ",\n" )

write( toJSON( x, digits = 16, auto_unbox = TRUE ), "./data.json" )
write( toJSON( y, digits = 16, auto_unbox = TRUE ), "./expected.json" )
