#!/usr/bin/env julia

import JSON

"""
    gen( x, filepath )

Generate fixture data and write to file.

# Arguments

* `x`: domain
* `filepath::AbstractString`: filepath of the output file

# Examples

``` julia
julia> x = linspace( -0.5, 0.5, 2001 );
julia> gen( x, \"./data.json\" );
```
"""
function gen( x, filepath )
	y = Array( Float64, length( x ) );
	for i in eachindex(x)
		y[i] = erfinv( x[i] );
	end

	data = Dict([
		("x", x),
		("expected", y)
	]);

	outfile = open( filepath, "w" );
	write( outfile, JSON.json(data) );
	close( outfile );
end

# Get the filename:
file = @__FILE__;

# Extract the directory in which this file resides:
dir = dirname( file );

# |x| <= 0.5
x = linspace( -0.5, 0.5, 3000 );
out = joinpath( dir, "x_-0.5_0.5.json" );
gen( x, out );

# 1-|x| > 0.25
x = linspace( 0.5, 0.75, 500 );
out = joinpath( dir, "x_0.5_0.75.json" );
gen( x, out );

x = linspace( -0.5, -0.75, 500 );
out = joinpath( dir, "x_-0.5_-0.75.json" );
gen( x, out );

# 0.75 < |x| < 0.9998765901959134
x = linspace( 0.75, 0.9998, 500 );
out = joinpath( dir, "x_0.75_0.9998.json" );
gen( x, out );

# 0.9998765901959134 < |x| < 0.9999999999999998
x = linspace( 0.9998, 0.9999999999999998, 500 );
out = joinpath( dir, "x_0.9998_0.9999..8.json" );
gen( x, out );

# 0.9999999999999998 < |x| < 1
x = linspace( 0.9999999999999998, 1, 500 );
out = joinpath( dir, "x_0.9999..8_1.json" );
gen( x, out );
