#!/usr/bin/env julia

import JSON

"""
    gen( x, name )

Generate fixture data and write to file.

# Arguments

* `x`: domain
* `name::AbstractString`: output filename

# Examples

``` julia
julia> x = linspace( -1000.0, 1000.0, 2001 );
julia> gen( x, \"data.json\" );
```
"""
function gen( x, name )
	y = Array( Float64, length( x ) );
	for i in eachindex(x)
		y[i] = lfact( x[i] );
	end

	# Store data to be written to file as a collection:
	data = Dict([
		("x", x),
		("expected", y)
	]);

	# Based on the script directory, create an output filepath:
	filepath = joinpath( dir, name );

	# Write the data to the output filepath as JSON:
	outfile = open( filepath, "w" );
	write( outfile, JSON.json(data) );
	close( outfile );
end

# Get the filename:
file = @__FILE__;

# Extract the directory in which this file resides:
dir = dirname( file );

# Positive small values:
x = linspace( 1.0, 3.0, 1001 );
gen( x, "small_positive.json" );

# Positive medium values:
x = linspace( 3.0, 20.0, 1001 );
gen( x, "medium_positive.json" );

# Large positive values:
x = collect( 500:25:5000 );
gen( x, "large_positive.json" );

# Very large positive values:
x = collect( 5000:50:10000 );
gen( x, "very_large_positive.json" );
