'use strict';

// MODULES //

var tape = require( 'tape' );
var isnan = require( '@stdlib/math/base/utils/is-nan' );
var incrspace = require( '@stdlib/math/generics/utils/incrspace' );
var abs = require( '@stdlib/math/base/special/abs' );
var PINF = require( '@stdlib/math/constants/float64-pinf' );
var NINF = require( '@stdlib/math/constants/float64-ninf' );
var factorialln = require( './../lib' );


// FIXTURES //

var x = require( './fixtures/r/data.json' );
var expected = require( './fixtures/r/expected.json' );
var v;
var i;
for ( i = 0; i < expected.length; i++ ) {
	v = expected[ i ];
	if ( v === 'Inf' ) {
		expected[ i ] = PINF;
	}
	else if ( v === 'NaN' ) {
		expected[ i ] = NaN;
	}
}


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof factorialln, 'function', 'main export is a function' );
	t.end();
});

tape( 'if provided a negative integer, the function returns `+Infinity`', function test( t ) {
	var values = incrspace( -1.0, -1000.0, -1.0 );
	var v;
	var i;

	for ( i = 0; i < values.length; i++ ) {
		v = factorialln( values[ i ] );
		t.equal( v, PINF, 'returns +Infinity when provided ' + values[ i ] );
	}
	t.end();
});

tape( 'if provided negative infinity, the function returns `+infinity`', function test( t ) {
	var v = factorialln( NINF );
	t.ok( v, PINF, 'returns +infinity when provided negative infinity' );
	t.end();
});

tape( 'if provided positive infinity, the function returns `+infinity`', function test( t ) {
	var v = factorialln( PINF );
	t.ok( v, PINF, 'returns +infinity when provided +infinity' );
	t.end();
});

tape( 'if provided `NaN`, the function returns `NaN`', function test( t ) {
	var v = factorialln( NaN );
	t.equal( isnan( v ), true, 'returns NaN when provided a NaN' );
	t.end();
});

tape( 'the function evaluates the natural logarithm of the factorial function', function test( t ) {
	var delta;
	var tol;
	var v;
	var i;

	for ( i = 0; i < x.length; i++ ) {
		v = factorialln( x[ i ] );
		if ( v === expected[ i ] ) {
			t.equal( v, expected[ i ], 'returns '+expected[i]+' when provided '+x[i] );
		} else {
			delta = abs( v - expected[ i ] );
			tol = 1.0e-14 * Math.max( 1.0, abs( v ), abs( expected[ i ] ) );
			t.ok( delta <= tol, 'within tolerance. x: ' + x[ i ] + '. Value: ' + v + '. Expected: ' + expected[ i ] + '. Tolerance: ' + tol + '.' );
		}
	}
	t.end();
});
