#include <random>
#include <algorithm>
#include <iterator>
#include <vector>
#include <iostream>
#include <boost/random/mersenne_twister.hpp>
#include <boost/random/uniform_int_distribution.hpp>
#include <boost/math/special_functions/factorials.hpp>

using namespace std;

boost::random::mt19937 gen;

/**
* Generates a random integer between one and two-hundred (inclusive).
*
* @return random integer
*/
int generate_integer() {
	boost::random::uniform_int_distribution<> dist( 1, 200 );
	return dist( gen );
}

/**
* Generates a linearly spaced numeric array.
*
* @param start   first array value
* @param end     last array value
* @param num     length of output array
* @return linearly spaced numeric array
*/
vector<double> linspace( double start, double end, int num ) {
	double delta = (end - start) / (num - 1);
	vector<double> arr( num - 1 );
	for ( int i = 0; i < num - 1; ++i ){
		arr[ i ] = start + delta * i;
	}
	arr.push_back( end );
	return arr;
}

/**
* Prints the elements of a vector as JSON.
*
* @param vec    input vector
* @param last   boolean indicating whether last vector in JSON object
* @return string representation of vector for JSON output
*/
void print_vector( vector<double> vec, bool last = false ) {
	int precision = std::numeric_limits<double>::max_digits10;
	cout << "[";
	for ( vector<double>::iterator it = vec.begin(); it != vec.end(); ++it ) {
		if ( vec.end() != it+1 ) {
			cout << setprecision( precision ) << *it;
			cout << ",";
		} else {
			cout << setprecision( precision ) << *it;
			cout << "]";
			if ( last == false ) {
				cout << ",";
			}
		}
	}
	return;
}

/**
* Prints the results as JSON.
*
* @param x          input values
* @param n          input values
* @param expected   falling factorial
* @return JSON string
*/
void print_results(
	vector<double> x,
	vector<double> n,
	vector<double> expected
) {
	cout << "{" << endl;
	cout << "  \"x\": ";
	print_vector( x );
	cout << "  \"n\": ";
	print_vector( n );
	cout << "  \"expected\": ";
	print_vector( expected, true );
	cout << "}" << endl;
	return;
}

/**
* Main execution sequence.
*/
int main() {
	random_device rd;
	mt19937 g(rd());

	vector<double> x = linspace( 0.0, 0.5, 1000 );
	shuffle( x.begin(), x.end(), g );
	vector<double> n(1000);
	vector<double> expected;

	for ( int i = 0; i < 1000; i++ ) {
		double arg1 = x[ i ];
		int arg2 = generate_integer();
		n[ i ] = (double) arg2;
		expected.push_back( boost::math::falling_factorial( arg1, arg2 ) );
	}

	print_results( x, n, expected );
	return 0;
}
