'use strict';

// MODULES //

var tape = require( 'tape' );
var PINF = require( '@stdlib/math/constants/float64-pinf' );
var NINF = require( '@stdlib/math/constants/float64-ninf' );
var isnan = require( '@stdlib/math/base/utils/is-nan' );
var floor = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof floor, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns the largest integer less than or equal to a given number', function test( t ) {
	t.equal( floor( -4.2 ), -5.0, 'equals -5' );
	t.equal( floor( 9.99999 ), 9.0, 'equals 9' );
	t.equal( floor( 0.0 ), 0.0, 'equals 0' );
	t.end();
});

tape( 'the function returns `NaN` if provided a `NaN`', function test( t ) {
	var v = floor( NaN );
	t.equal( isnan( v ), true, 'returns NaN' );
	t.end();
});

tape( 'the function returns `+infinity` if provided a `+infinity`', function test( t ) {
	var val = floor( PINF );
	t.equal( val, PINF, 'returns +infinity' );
	t.end();
});

tape( 'the function returns `-infinity` if provided a `-infinity`', function test( t ) {
	var val = floor( NINF );
	t.equal( val, NINF, 'returns -infinity' );
	t.end();
});
