#include <random>
#include <algorithm>
#include <iterator>
#include <vector>
#include <iostream>
#include <boost/math/special_functions/gamma.hpp>

using namespace std;

vector<double> linspace( double start, double end, int num ) {
	double delta = (end - start) / (num - 1);
	vector<double> arr( num - 1 );
	for ( int i = 0; i < num - 1; ++i ){
		arr[ i ] = start + delta * i;
	}
	arr.push_back( end );
	return arr;
}

void print_vector( vector<double> vec, bool last = false ) {
	int precision = std::numeric_limits<double>::max_digits10;
	cout << "[";
	for ( vector<double>::iterator it = vec.begin(); it != vec.end(); ++it ) {
		if ( vec.end() != it+1 ) {
			cout << setprecision( precision ) << *it;
			cout << ",";
		} else {
			cout << setprecision( precision ) << *it;
			cout << "]";
			if ( last == false ) {
				cout << ",";
			}
		}
	}
	return;
}

void print_results(
	vector<double> x,
	vector<double> expected
) {
	cout << "{" << endl;
	cout << "  \"x\": ";
	print_vector( x );
	cout << "  \"expected\": ";
	print_vector( expected, true );
	cout << "}" << endl;
	return;
}

int main() {
	random_device rd;
	mt19937 g(rd());

	vector<double> x = linspace( 0.0, 2.0, 500 );
	vector<double> expected;

	for ( int i = 0; i < 500; i++ ) {
		double arg = x[ i ];
		expected.push_back( boost::math::tgamma1pm1( arg ) );
	}

	print_results( x, expected );
	return 0;
}
