'use strict';

var randu = require( '@stdlib/math/base/random/randu' );
var round = require( '@stdlib/math/base/special/round' );
var gcd = require( './../lib' );

var a;
var b;
var v;
var i;

for ( i = 0; i < 100; i++ ) {
	a = round( randu()*50.0 );
	b = round( randu()*50.0 );
	v = gcd( a, b );
	console.log( 'gcd(%d,%d) = %d', a, b, v );
}
