/**
* Compute the hypotenuse avoiding overflow and underflow.
*/
#include <math.h>
#include "hypot.h"

/**
* Computes the hypotenuse avoiding overflow and underflow.
*
* @param x       number
* @param y       number
* @return        hypotenuse
*/
double c_hypot( const double x, const double y ) {
	double tmp;
	double a;
	double b;
	if ( isnan( x ) || isnan( y ) ) {
		return NAN;
	}
	if ( isinf( x ) || isinf( y ) ) {
		return INFINITY;
	}
	a = x;
	b = y;
	if ( a < 0.0 ) {
		a = -a;
	}
	if ( b < 0.0 ) {
		b = -b;
	}
	if ( a < b ) {
		tmp = b;
		b = a;
		a = tmp;
	}
	if ( a == 0.0 ) {
		return 0.0;
	}
	b /= a;
	return a * sqrt( 1.0 + (b*b) );
}
