/**
* Compute the hypotenuse avoiding overflow and underflow.
*/
#include "stdlib_base_hypot.h"
#include <math.h>
#include "stdlib_base_is_nan.h"
#include "stdlib_base_is_infinite.h"

/**
* Computes the hypotenuse avoiding overflow and underflow.
*
* @param x       number
* @param y       number
* @return        hypotenuse
*/
double stdlib_base_hypot( const double x, const double y ) {
	double tmp;
	double a;
	double b;
	if ( stdlib_base_is_nan( x ) || stdlib_base_is_nan( y ) ) {
		return NAN;
	}
	if ( stdlib_base_is_infinite( x ) || stdlib_base_is_infinite( y ) ) {
		return INFINITY;
	}
	a = x;
	b = y;
	if ( a < 0.0 ) {
		a = -a;
	}
	if ( b < 0.0 ) {
		b = -b;
	}
	if ( a < b ) {
		tmp = b;
		b = a;
		a = tmp;
	}
	if ( a == 0.0 ) {
		return 0.0;
	}
	b /= a;
	return a * sqrt( 1.0 + (b*b) );
}
