'use strict';

var randu = require( '@stdlib/math/base/random/randu' );
var round = require( '@stdlib/math/base/special/round' );
var absInt32 = require( './../lib' );

var x;
var i;

for ( i = 0; i < 100; i++ ) {
	x = round( randu() * 100.0 ) - 50;
	console.log( 'abs(%d) = %d', x, absInt32( x|0 ) );
}
