// MAIN //

/**
* Computes an absolute value of a signed 32-bit integer.
*
* @private
* @param {integer32} x - integer
* @returns {integer32} absolute value
*
* @example
* var v = iabs( -10 );
* // returns 10
*/
function iabs( x ) {
	var mask = ( x >> 31 );
	return ((x ^ mask) - mask)|0; // asm type annotation
} // end FUNCTION iabs()


// EXPORTS //

module.exports = iabs;
