'use strict';

/**
* Compute the multiplicative inverse.
*
* @module @stdlib/math/base/special/inv
*
* @example
* var inv = require( '@stdlib/math/base/special/inv' );
*
* var v = inv( -1.0 );
* // returns -1.0
*
* v = inv( 2.0 );
* // returns 0.5
*
* v = inv( 0.0 );
* // returns Number.POSITIVE_INFINITY
*
* v = inv( -0.0 );
* // returns Number.NEGATIVE_INFINITY
*
* v = inv( NaN );
* // returns NaN
*/

// MODULES //

var inv = require( './inv.js' );


// EXPORTS //

module.exports = inv;
