'use strict';

/**
* Computes the multiplicative inverse of `x`.
*
* @param {number} x - input value
* @returns {number} multiplicative inverse
*
* @example
* var v = inv( -1.0 );
* // returns -1.0
*
* @example
* var v = inv( 2.0 );
* // returns 0.5
*
* @example
* var v = inv( 0.0 );
* // returns Number.POSITIVE_INFINITY
*
* @example
* var v = inv( -0.0 );
* // returns Number.NEGATIVE_INFINITY
*
* @example
* var v = inv( NaN );
* // returns NaN
*/
function inv( x ) {
	return 1.0 / x;
} // end FUNCTION inv()


// EXPORTS //

module.exports = inv;
