'use strict';

var randu = require( '@stdlib/random/base/randu' );
var kernelBetainc = require( './../lib' );

var out;
var i;
var x;
var a;
var b;

out = new Array( 2 );
for ( i = 0; i < 100; i++ ) {
	x = randu();
	a = randu() * 10.0;
	b = randu() * 10.0;
	out = kernelBetainc( out, x, a, b, true, false );
	console.log( 'x: %d, \t a: %d, \t b: %d, \t f(x,a,b): %d, \t f^1(x,a,b): %d',
		x.toFixed( 4 ),
		a.toFixed( 4 ),
		b.toFixed( 4 ),
		out[ 0 ].toFixed( 4 ),
		out[ 1 ].toFixed( 4 )
	);
}
