/**
* Evaluate the Kronecker delta.
*/
#include <math.h>
#include "isnan.h"
#include "kronecker_delta.h"

/**
* Evaluates the Kronecker delta.
*
* @param i       input value
* @param j       input value
* @return        function value
*/
double stdlib_base_kronecker_delta( const double i, const double j ) {
	if ( stdlib_base_isnan( i ) || stdlib_base_isnan( j ) ) {
		return NAN;
	}
	if ( i == j ) {
		return 1.0;
	}
	return 0.0;
}
