/**
* Evaluate the Kronecker delta.
*/
#include "stdlib_base_kronecker_delta.h"
#include <math.h>
#include "is_nan.h"

/**
* Evaluates the Kronecker delta.
*
* @param i       input value
* @param j       input value
* @return        function value
*/
double stdlib_base_kronecker_delta( const double i, const double j ) {
	if ( stdlib_base_is_nan( i ) || stdlib_base_is_nan( j ) ) {
		return NAN;
	}
	if ( i == j ) {
		return 1.0;
	}
	return 0.0;
}
