'use strict';

// MODULES //

var tape = require( 'tape' );
var PINF = require( '@stdlib/math/constants/float64-pinf' );
var NINF = require( '@stdlib/math/constants/float64-ninf' );
var isnan = require( '@stdlib/math/base/utils/is-nan' );
var ln = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof ln, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function computes the natural logarithm of a nonnegative number', function test( t ) {

	// TODO: more extensive testing against R, Julia, etc

	// Tested on Wolfram Alpha...
	t.equal( ln( 4.0 ), 1.3862943611198906188344642429163531361510002687205105, 'equals ~1.386' );
	t.equal( ln( 9.0 ), 2.1972245773362193827904904738450514092949811156454989, 'equals ~2.197' );
	t.end();
});

tape( 'the function returns `-infinity` if provided `0`', function test( t ) {
	t.equal( ln( 0.0 ), NINF, 'equals -infinity' );
	t.end();
});

tape( 'the function returns `+infinity` if provided `+infinity`', function test( t ) {
	t.equal( ln( PINF ), PINF, 'equals +infinity' );
	t.end();
});

tape( 'the function returns `NaN` if provided a negative number', function test( t ) {
	var v = ln( -1.0 );
	t.equal( isnan( v ), true, 'returns NaN' );
	t.end();
});
