#!/usr/bin/env julia

import JSON

"""
    gen( x, name )

Generate fixture data and write to file.

# Arguments

* `x`: input values
* `name::AbstractString`: output filename

# Examples

``` julia
julia> x = rand( 1000 )*1000.0 - 500.0;
julia> gen( x, \"data.json\" );
```
"""
function gen( x, name )
	len = length( x );

	frac = Array( Float64, len );
	integral = Array( Float64, len );

	for i in eachindex(x)
		out = modf( x[i] );
		frac[ i ] = out[ 1 ];
		integral[ i ] = out[ 2 ];
	end

	# Store data to be written to file as a collection:
	data = Dict([
		("x", x),
		("frac", frac),
		("integral", integral)
	]);

	# Based on the script directory, create an output filepath:
	filepath = joinpath( dir, name );

	# Write the data to the output filepath as JSON:
	outfile = open( filepath, "w" );
	write( outfile, JSON.json(data) );
	close( outfile );
end

# Get the filename:
file = @__FILE__;

# Extract the directory in which this file resides:
dir = dirname( file );

# Generate normal values:
x = linspace( -1000, 1000, 1003 );
gen( x, "medium.json" );

# Generate small values:
x = linspace( -1.0, 1.0, 1003 );
gen( x, "small.json" );

# Generate subnormal values:
x = linspace( 1.0e-314, 1.0e-315, 1003 );
gen( x, "subnormal.json" );

# Generate large values:
x = linspace( 2.0^21, 2.0^51, 1003 );
gen( x, "large.json" );

# Generate huge values:
x = linspace( 1.0e299, 1.0e301, 1003 );
gen( x, "huge.json" );
