'use strict';

var round = require( '@stdlib/math/base/special/round' );
var powm1 = require( './../lib' );

var b;
var x;
var y;
var i;

for ( i = 0; i < 100; i++ ) {
	b = round( Math.random()*10.0 );
	x = round( Math.random()*10.0 ) - 5.0;
	y = powm1( b, x );
	console.log( '%d^%d - 1 = %d', b, x, y );
}
