'use strict';

var randu = require( '@stdlib/random/base/randu' );
var PI = require( '@stdlib/constants/math/float64-pi' );
var rad2deg = require( './../lib' );

var r;
var d;
var i;

for ( i = 0; i < 100; i++ ) {
	r = randu() * 2.0 * PI;
	d = rad2deg( r );
	console.log( 'radians: %d => degrees: %d', r, d );
}
