'use strict';

// MODULES //

var tape = require( 'tape' );
var NINF = require( '@stdlib/math/constants/float64-ninf' );
var PINF = require( '@stdlib/math/constants/float64-pinf' );
var round = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof round, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function rounds a numeric value to the nearest integer', function test( t ) {
	t.equal( round( -4.2 ), -4, 'equals -4' );
	t.equal( round( -4.5 ), -4, 'equals -4' );
	t.equal( round( -4.8 ), -5, 'equals -5' );
	t.equal( round( 4.2 ), 4, 'equals 4' );
	t.equal( round( 9.99999 ), 10, 'equals 10' );
	t.equal( round( 9.5 ), 10, 'equals 10' );
	t.equal( round( 9.4 ), 9, 'equals 10' );
	t.equal( round( 0 ), 0, 'equals 0' );
	t.end();
});

tape( 'the function returns `NaN` if provided a `NaN`', function test( t ) {
	var val = round( NaN );
	t.ok( val !== val, 'returns NaN' );
	t.end();
});

tape( 'the function returns `+infinity` if provided a `+infinity`', function test( t ) {
	var val = round( PINF );
	t.equal( val, PINF, 'returns +infinity' );
	t.end();
});

tape( 'the function returns `-infinity` if provided a `-infinity`', function test( t ) {
	var val = round( NINF );
	t.equal( val, NINF, 'returns -infinity' );
	t.end();
});
