'use strict';

var round = require( '@stdlib/math/base/special/round' );
var signum = require( './../lib' );

var sign;
var v;
var i;

for ( i = 0; i < 100; i++ ) {
	v = round( Math.random()*100 ) - 50;
	if ( signum( v ) < 0 ) {
		sign = '-';
	} else {
		sign = '+';
	}
	console.log( 'signum(%d) = %s', v, sign );
}
