'use strict';

// MODULES //

var isnan = require( '@stdlib/math/base/utils/is-nan' );


// SIGNUM //

/**
* FUNCTION: signum( x )
*	Determines the sign of a numeric value.
*
* @param {Number} x - input value
* @returns {Number} value indicating the sign
*/
function signum( x ) {
	if ( x === 0 || isnan( x ) ) {
		return x; // addresses both +-0
	}
	return ( x < 0 ) ? -1 : 1;
} // end FUNCTION signum()


// EXPORTS //

module.exports = signum;
