#!/usr/bin/env julia

import JSON

"""
    gen( x, filepath )

Generate fixture data and write to file.

# Arguments

* `x`: domain
* `filepath::AbstractString`: filepath of the output file

# Examples

``` julia
julia> x = linspace( -1000, 1000, 2001 );
julia> gen( x, \"./data.json\" );
```
"""
function gen( x, filepath )
	y = trigamma( x );
	data = Dict([
		("x", x),
		("expected", y)
	]);
	outfile = open( filepath, "w" );
	write( outfile, JSON.json(data) );
	close( outfile );
end

# Get the filename:
file = @__FILE__;

# Extract the directory in which this file resides:
dir = dirname( file );

# Generate fixture data for positive values:
x = linspace( 0.0, 200.0, 2000 );
out = joinpath( dir, "positive.json" );
gen( x, out );

# Generate fixture data for negative values:
x = linspace( -200.0, 0.0, 2000 )
out = joinpath( dir, "negative.json" );
gen( x, out );
