'use strict';

/**
* Round a numeric value to the nearest power of 10 toward zero.
*
* @module @stdlib/math/base/special/truncate10
*
* @example
* var truncate10 = require( '@stdlib/math/base/special/truncate10' );
*
* var v = truncate10( Math.PI );
* // returns 1.0
*
* v = truncate10( 13.0 );
* // returns 10.0
*
* v = truncate10( -0.314 );
* // returns -0.1
*/

// MODULES //

var truncate10 = require( './truncate10.js' );


// EXPORTS //

module.exports = truncate10;
