'use strict';

/**
* Round a numeric value to the nearest power of two toward zero.
*
* @module @stdlib/math/base/special/truncate2
*
* @example
* var truncate2 = require( '@stdlib/math/base/special/truncate2' );
*
* var v = truncate2( Math.PI );
* // returns 2.0
*
* v = truncate2( 13.0 );
* // returns 8.0
*
* v = truncate2( -0.314 );
* // returns -0.25
*/

// MODULES //

var truncate2 = require( './truncate2.js' );


