'use strict';

var randu = require( '@stdlib/math/base/random/randu' );
var truncaten = require( './../lib' );

var x;
var n;
var v;
var i;

for ( i = 0; i < 100; i++ ) {
	x = (randu()*100.0) - 50.0;
	n = truncaten( randu()*5.0, 0 );
	v = truncaten( x, -n );
	console.log( 'x: %d. Number of decimals: %d. Rounded: %d.', x, n, v );
}
