'use strict';

var continuedFraction = require( './../lib' );
var out;

out = continuedFraction( closure(), {
	'keep': true
});
console.log( 'Golden ratio (closure): %d', out );

out = continuedFraction( generator(), {
	'keep': true
});
console.log( 'Golden ratio (generator): %d,', out );

// TODO: remove once switch to ESLint is complete
/* jshint esnext: true */
function* generator() {
	while ( true ) {
		yield [ 1, 1 ];
	}
}

function closure() {
	var ones = [ 1, 1 ];
	return function() {
		return ones;
	};
}
