'use strict';

var hasGeneratorsSupport = require( '@stdlib/utils/detect-generator-support' )();
var continuedFraction = require( './../lib' );
var out;

// GOLDEN RATIO //

if ( hasGeneratorsSupport ) {
	require( './es2015-generator' );
} else {
	out = continuedFraction( closure(), {
		'keep': true
	});
	console.log( 'Golden ratio (closure): %d', out );
}

function closure() {
	var ones = [ 1, 1 ];
	return function() {
		return ones;
	};
}
