'use strict';

var resolve = require( 'path' ).resolve;
var randu = require( '@stdlib/random/base/randu' );
var round = require( '@stdlib/math/base/special/round' );
var Float64Array = require( '@stdlib/array/float64' );
var tryRequire = require( '@stdlib/utils/try-require' );

var compile = tryRequire( resolve( __dirname, '..', 'lib' ) );
if ( compile instanceof Error ) {
	console.log( 'Unable to run example. Unsupported environment.' );
} else {
	main();
}

function main() {
	var coef;
	var sign;
	var str;
	var i;

	// Create an array of random coefficients...
	coef = new Float64Array( 10 );
	for ( i = 0; i < coef.length; i++ ) {
		if ( randu() < 0.5 ) {
			sign = -1.0;
		} else {
			sign = 1.0;
		}
		coef[ i ] = sign * round( randu()*100.0 );
	}

	// Compile a module for evaluating a polynomial:
	str = compile( coef );
	console.log( str );
}
