'use strict';

/**
* Evaluate a Fibonacci polynomial.
*
* @module @stdlib/math/base/tools/fibpoly
*
* @example
* var evalfibpoly = require( '@stdlib/math/base/tools/fibpoly' );
*
* var v = evalfibpoly( 5, 1.0 );
* // returns 5.0
*
* @example
* var factory = require( '@stdlib/math/base/tools/fibpoly' ).factory;
*
* var fibpolyval = factory( 5 );
*
* var v = fibpolyval( 1.0 );
* // returns 5.0
*
* v = fibpolyval( 2.0 );
* // returns 29.0
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var evalfibpoly = require( './main.js' );
var factory = require( './factory.js' );


// MAIN //

setReadOnly( evalfibpoly, 'factory', factory );


// EXPORTS //

module.exports = evalfibpoly;
