'use strict';

// MODULES //

var abs = require( '@stdlib/math/base/special/abs' );


// CONSTANTS //

var TOLERANCE = require( '@stdlib/math/constants/float64-eps' );
var MAX_TERMS = 1000000;


// SUM SERIES //

/**
* Sum the element of the series given by the supplied function.
*
* @param {Function} generator - series function
* @param {Object} [opts] - function options
* @param {Number} [opts.maxTerms=1000000] - maximum number of terms to be added
* @param {Number} [opts.tolerance=2.22e-16] - further terms are only added as long as the next term is greater than current term times the tolerance
* @param {Number} [opts.initialValue=0] - initial value of the resulting sum
* @returns {Number} sum of all series terms
*
* @example
* var gen = geometricSeriesGenerator( 0.9 );
* var out = sumSeries( gen );
* // returns 10
*
* function* geometricSeriesGenerator( x ) {
* 	var exponent = 0;
* 	while ( true ) {
* 		yield Math.pow( x, exponent );
* 		exponent += 1;
* 	}
* }
*/
function sum_series( generator, options ) {
	var isgenerator;
	var next_term;
	var tolerance;
	var counter;
	var result;
	var opts;

	opts = {};
	if ( arguments.length > 1 ) {
		opts = options;
	}
	tolerance = opts.tolerance || TOLERANCE;
	counter = opts.maxTerms || MAX_TERMS;
	result = opts.initialValue || 0;

	isgenerator = typeof generator.next === 'function';
	if ( isgenerator === true ) {
		// Case A: Iterate over generator object created by a generator function...
		// TODO: remove once switch to ESLint is complete
		/* jshint esnext: true */
		for ( next_term of generator ) {
			result += next_term;
			if ( (abs(tolerance * result) >= abs(next_term) ) || --counter === 0 ) {
				break;
			}
		}
	} else {
		// Case B: Repeatedly call function...
		do {
			next_term = generator();
			result += next_term;
		}
		while( ( abs(tolerance * result) < abs(next_term) ) && --counter );
	}
	return result;
} // end FUNCTION sum_series()


// EXPORTS //

module.exports = sum_series;
