'use strict';

/**
* Computes the absolute difference.
*
* @module @stdlib/math/base/utils/absolute-difference
*
* @example
* var diff = require( '@stdlib/math/base/utils/absolute-difference' );
*
* var d = diff( 2, 5 );
* // returns 3
*
* d = diff( -1, 3.14 );
* // returns ~4.14
*
* d = diff( 10.1, -2.05 );
* // returns ~12.15
*
* d = diff( -0, 0 );
* // returns 0
*
* d = diff( NaN, 5 );
* // returns NaN
*
* d = diff( +Infinity, -Infinity );
* // returns +infinity
* 
* d = diff( +Infinity, +Infinity );
* // returns NaN
*/

// MODULES //

var absoluteDifference = require( './absolutedifference.js' );


// EXPORTS //

module.exports = absoluteDifference;
