'use strict';

// MODULES //

var SMALLEST_FLOAT32 = require( '@stdlib/math/constants/float32-smallest' ).VALUE;
var pinf = require( '@stdlib/math/constants/float32-pinf' );
var ninf = require( '@stdlib/math/constants/float32-ninf' );
var abs = require( '@stdlib/math/base/special/abs' );
var toFloat32 = require( '@stdlib/math/base/utils/float64-to-float32' );


// CONSTANTS //

// (1<<32)
var SCALAR = 8388608;


// NORMALIZE //

/**
* FUNCTION: normalize( x )
*	Returns a normal number `y` and exponent `exp` satisfying `x = y * 2^exp`.
*
* @param {Number} x - single-precision floating-point number
* @returns {Number[]|Null} a two-element array containing `y` and `exp`
*/
function normalize( x ) {
	if (
		x !== x ||
		x === pinf ||
		x === ninf
	) {
		return [ x, 0 ];
	}
	if ( x !== 0 && abs( x ) < SMALLEST_FLOAT32 ) {
		x = toFloat32( x*SCALAR );
		return [ x, -23 ];
	}
	return [ x, 0 ];
} // end FUNCTION normalize()


// EXPORTS //

module.exports = normalize;
