'use strict';

// VARIABLES //

var FLOAT32_VIEW = new Float32Array( 1 );
var UINT32_VIEW = new Uint32Array( FLOAT32_VIEW.buffer );


// FLOAT32-TO-WORD //

/**
* Returns an unsigned 32-bit integer corresponding to the IEEE 754 binary representation of a single-precision floating-point number.
*
* @param {number} x - floating-point number
* @returns {number} 32-bit integer
*
* @example
* var float64ToFloat32 = require( '@stdlib/math/base/utils/float64-to-float32' );
* 
* var f32 = float64ToFloat32( 1.337 );
* // returns 1.3370000123977661
*
* var w = toWord( f32 );
* // returns 1068180177 => 0 01111111 01010110010001011010001
*/
function toWord( x ) {
	FLOAT32_VIEW[ 0 ] = x;
	return UINT32_VIEW[ 0 ];
} // end FUNCTION toWord()


// EXPORTS //

module.exports = toWord;
