'use strict';

var EPS = require( '@stdlib/math/constants/float64-eps' );
var epsdiff = require( './../lib' );

var sign;
var x;
var y;
var d;
var i;

for ( i = 0; i < 100; i++ ) {
	x = Math.random();
	sign = ( Math.random() > 0.5 ) ? 1.0 : -1.0;
	y = x + sign*EPS*i;
	d = epsdiff( x, y );
	console.log( 'x = %d. y = %d. d = %dÎµ.', x, y, d );
}
