'use strict';

// MODULES //

var getHighWord = require( '@stdlib/math/base/utils/float64-get-high-word' );


// VARIABLES //

// Exponent mask: 01111111111100000000000000000000
var EXP_MASK = 0x7ff00000;
var BIAS = 1023; // FIXME: constant


// EXPONENT //

/**
* Returns an integer corresponding to the unbiased exponent of a double-precision floating-point number.
*
* @param {number} x - input value
* @returns {number} unbiased exponent
*
* @example
* var exp = exponent( 3.14e-307 );
* // returns -1019 => 2**-1019 ~ 1e-307
* @example
* var exp = exponent( -3.14 );
* // returns 1
* @example
* var exp = exponent( 0 );
* // returns 0
* @example
* var exp = exponent( NaN );
* // returns 1024 => 2**1024 ~ infinity
*/
function exponent( x ) {
	// Extract from the input value a higher order word (unsigned 32-bit integer) which contains the exponent:
	var high = getHighWord( x );

	// Apply a mask to isolate only the exponent bits and then shift off all bits which are part of the fraction:
	high = ( high & EXP_MASK ) >>> 20;

	// Remove the bias and return:
	return high - BIAS;
} // end FUNCTION exponent()


// EXPORTS //

module.exports = exponent;
