'use strict';

// MODULES //

var FLOAT64_SMALLEST_NORMAL = require( '@stdlib/math/constants/float64-smallest-normal' );
var isInfinite = require( '@stdlib/math/base/utils/is-infinite' );
var isnan = require( '@stdlib/math/base/utils/is-nan' );
var abs = require( '@stdlib/math/base/special/abs' );


// CONSTANTS //

// (1<<52)
var SCALAR = 4503599627370496;


// NORMALIZE //

/**
* Returns a normal number `y` and exponent `exp` satisfying `x = y * 2^exp`.
*
* @param {number} x - input value
* @returns {number[]|null} a two-element array containing `y` and `exp`
*
* @example
* var pow = require( '@stdlib/math/base/special/pow' );
*
* var out = normalize( 3.14e-319 );
* // returns [ 1.4141234400356668e-303, -52 ]
*
* var y = out[ 0 ];
* var exp = out[ 1 ];
*
* var bool = ( y*pow(2,exp) === 3.14e-319 );
* // returns true
* @example
* var out = normalize( 0 );
* // returns [ 0, 0 ];
* @example
* var out = normalize( +Infinity );
* // returns [ +infinity, 0 ]
* @example
* var out = normalize( -Infinity );
* // returns [ -infinity, 0 ]
* @example
* var out = normalize( NaN );
* // returns [ NaN, 0 ]
*/
function normalize( x ) {
	if ( isnan( x ) || isInfinite( x ) ) {
		return [ x, 0 ];
	}
	if ( x !== 0 && abs( x ) < FLOAT64_SMALLEST_NORMAL ) {
		return [ x*SCALAR, -52 ];
	}
	return [ x, 0 ];
} // end FUNCTION normalize()


// EXPORTS //

module.exports = normalize;
