'use strict';

var randu = require( '@stdlib/math/base/random/randu' );
var signbit = require( './../lib' );

var sign;
var x;
var i;

for ( i = 0; i < 100; i++ ) {
	x = ( randu()*100.0 ) - 50.0;
	sign = signbit( x );
	sign = ( sign ) ? 'true' : 'false';
	console.log( 'x: %d. signbit: %s.', x, sign );
}
