'use strict';

// MODULES //

var randu = require( '@stdlib/math/base/random/randu' );
var round = require( '@stdlib/math/base/special/round' );
var isEvenFloat64 = require( '@stdlib/math/base/utils/is-even' );
var isEvenInt32 = require( './../lib' );


// VARIABLES //

var N = 1e7;


// FUNCTIONS //

function warmup() {
	var i;
	var j;

	j = 0;
	for ( i = 0; i < 1e6; i++ ) {
		j += i * 2;
	}
	if ( j > 0 ) {
		return true;
	}
	return false;
}

function test( name, fcn ) {
	var start;
	var time;
	var bool;
	var x;
	var y;
	var i;

	bool = warmup();
	if ( !bool ) {
		throw new Error( 'Something went wrong.' );
	}
	start = process.hrtime();
	for ( i = 0; i < N; i++ ) {
		x = round( randu()*100.0 );
		y = fcn( x );
		if ( y !== false && y !== true ) {
			throw new Error( 'Something went wrong.' );
		}
	}
	time = process.hrtime( start );
	time = time[ 0 ] + time[ 1 ]*1.0e-9;

	if ( y !== false && y !== true ) {
		throw new Error( 'Something went wrong.' );
	}
	console.log( 'Test: %s. Elapsed time: %d sec. Iterations: %d. ops/s: %d.', name, time, N, N/time );
}

function benchmark() {
	test( 'float64', isEvenFloat64 );
	test( 'int32', isEvenInt32 );

	test( 'float64', isEvenFloat64 );
	test( 'int32', isEvenInt32 );

	test( 'float64', isEvenFloat64 );
	test( 'int32', isEvenInt32 );
}


// MAIN //

benchmark();
