'use strict';

/**
* Test if a numeric value is an even number.
*
* @module @stdlib/math/base/utils/is-even
*
* @example
* var isEven = require( '@stdlib/math/base/utils/is-even' );
*
* var bool = isEven( 5.0 );
* // returns false
*
* bool = isEven( -2.0 );
* // returns true
*
* bool = isEven( +Infinity );
* // returns false
*
* bool = isEven( -Infinity );
* // returns false
*
* bool = isEven( NaN );
* // returns false
*/

// MODULES //

var isEven = require( './iseven.js' );


// EXPORTS //

module.exports = isEven;
