'use strict';

// MODULES //

var isInfinite = require( '@stdlib/math/base/utils/is-infinite' );
var isInteger = require( '@stdlib/math/base/utils/is-integer' );


// IS EVEN //

/**
* Tests if a numeric value is an even number.
*
* @param {number} x - value to test
* @returns {boolean} boolean indicating whether the value is an even number
*
* @example
* var bool = isEven( 5.0 );
* // returns false
* @example
* var bool = isEven( -2.0 );
* // returns true
* @example
* var bool = isEven( +Infinity );
* // returns false
* @example
* var bool = isEven( -Infinity );
* // returns false
* @example
* var bool = isEven( NaN );
* // returns false
*/
function isEven( x ) {
	return (
		!isInfinite( x ) &&
		isInteger( x/2.0 )
	);
} // end FUNCTION isEven()


// EXPORTS //

module.exports = isEven;
