'use strict';

/**
* Tests if a numeric value is finite.
*
* @module @stdlib/utils/is-finite
*
* @example
* var isfinite = require( '@stdlib/math/base/utils/is-finite' );
*
* var v = isfinite( 5 );
* // returns true
*
* v = isfinite( -2.0e64 );
* // returns true
*
* v = isfinite( +Infinity );
* // returns false
*
* v = isfinite( -Infinity );
* // returns false
*/

// MODULES //

var isfinite = require( './isfinite.js' );


// EXPORTS //

module.exports = isfinite;
