'use strict';

// MODULES //

var PINF = require( '@stdlib/math/constants/float64-pinf' );
var NINF = require( '@stdlib/math/constants/float64-ninf' );


// IS FINITE //

/**
* Tests if a numeric value is finite.
*
* @param {number} x - value to be tested
* @returns {boolean} boolean indicating whether the value is finite
*
* @example
* var v = isfinite( 5 );
* // returns true
* @example
* var v = isfinite( -2.0e64 );
* // returns true
* @example
* var v = isfinite( +Infinity );
* // returns false
* @example
* var v = isfinite( -Infinity );
* // returns false
*/
function isfinite( x ) {
	return (
		// NaN check (x !== x ):
		x === x &&

		// +-infinity check:
		x > NINF &&
		x < PINF
	);
} // end FUNCTION isfinite()


// EXPORTS //

module.exports = isfinite;
